import React, { useState } from "react";

const test = () => {
  //   const labelFloat = () => {
  //     if (this.value !== "") {
  //       this.addClass("filled");
  //     } else {
  //       this.removeClass("filled");
  //     }
  //   };
  const log = () => {
    console.log("test");
  };

  const [isActive, setIsActive] = useState(false);
  const [value, setValue] = useState("");
  function handleTextChange(text) {
    setValue(text);

    if (text !== "") {
      setIsActive(true);
    } else {
      setIsActive(false);
    }
  }

  return (
    <div className="flex flex-col">
      <button className="btn btnRed">Rouge</button>
      <button className="btn btnBlue">Bleu</button>
      <button className="btn btnGreen">Vert</button>

      <form>
        <div className="inputText">
          <input type="text" id="name" onClick={log} />
          <label htmlFor="name">First name</label>
        </div>
      </form>
      <section className="m-12">
        /*
        --------------------------------------------------------------------------
        */ /* j */ /*
        --------------------------------------------------------------------------
        */
      </section>
      <div id="float-label">
        <input
          type="text"
          value={value}
          onChange={(e) => handleTextChange(e.target.value)}
        />

        <label className={isActive ? "Active" : ""} htmlFor="test">
          test
        </label>
      </div>
    </div>
  );
};

export default test;
